import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import { countries, CountryCode } from 'countries-list'; // Import the countries data and CountryCode type
import './App.css';

const App: React.FC = () => {
  const [weatherData, setWeatherData] = useState<any | null>(null);
  const [selectedLocation, setSelectedLocation] = useState<CountryCode | null>(null); // Update the type to CountryCode
  const apiUrl: string = 'http://localhost:8000/api/weather';

  useEffect(() => {
    const fetchWeatherData = async (): Promise<void> => {
      try {
        if (selectedLocation) {
          const response = await axios.get(`${apiUrl}/${selectedLocation}`);
          setWeatherData(response.data);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchWeatherData();
  }, [selectedLocation]);

  const handleLocationChange = (selectedOption: any) => {
    setSelectedLocation(selectedOption.value);
  };

  // Prepare the country options array
  const countryOptions = Object.keys(countries).map((code: CountryCode) => { // Update the code parameter type to CountryCode
    return {
      value: code,
      label: countries[code],
    };
  });

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Weather App</h1>
      </header>
      <main className="app-content">
        <div className="location-dropdown">
          <Select
            placeholder="Select a location"
            options={countryOptions} // Use the country options array
            onChange={handleLocationChange}
          />
        </div>
        {weatherData && weatherData.current ? (
          <div>
            <h2>Current Weather</h2>
            <div className="weather-card">
              <h3>{weatherData.location.name}</h3>
              <p className="temperature">{weatherData.current.temp_c}Â°C</p>
              <p className="condition">{weatherData.current.condition.text}</p>
            </div>
          </div>
        ) : (
          <div>Loading...</div>
        )}
      </main>
      <footer className="app-footer">
        <p>Powered by React</p>
      </footer>
    </div>
  );
};

export default App;
