import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './App.css';

interface WeatherData {
  location: {
    name: string;
  };
  current: {
    temp_c: number;
    condition: {
      text: string;
    };
  };
}

interface CityOption {
  value: string;
  label: string;
}

const libraries: ('places' | 'drawing' | 'geometry' | 'localContext' | 'visualization')[] = ['places'];
const googleMapsApiKey = 'YOUR_API_KEY';

const App: React.FC = () => {
  const [weatherData, setWeatherData] = useState<WeatherData | null>(null);
  const [selectedLocation, setSelectedLocation] = useState<string | null>(null);
  const [mapCenter, setMapCenter] = useState<google.maps.LatLngLiteral | null>(null);

  useEffect(() => {
    const fetchWeatherData = async (): Promise<void> => {
      try {
        if (selectedLocation) {
          const response = await axios.get<WeatherData>(`http://localhost:8000/api/weather/${selectedLocation}`);
          setWeatherData(response.data);
          updateMap(response.data.location.name);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchWeatherData();
  }, [selectedLocation]);

  const handleLocationChange = (selectedOption: CityOption | null) => {
    if (selectedOption) {
      setSelectedLocation(selectedOption.value);
    } else {
      setSelectedLocation(null);
    }
  };

  const updateMap = (location: string) => {
    const geocoder = new google.maps.Geocoder();
    geocoder.geocode({ address: location }, (results, status) => {
      if (status === google.maps.GeocoderStatus.OK && results && results[0].geometry) {
        const lat = results[0].geometry.location.lat();
        const lng = results[0].geometry.location.lng();
        setMapCenter({ lat, lng });
      }
    });
  };

  const cities: CityOption[] = [
    { value: 'london', label: 'London' },
    { value: 'manchester', label: 'Manchester' },
    { value: 'birmingham', label: 'Birmingham' },
    { value: 'glasgow', label: 'Glasgow' },
    { value: 'liverpool', label: 'Liverpool' },
    // Add more cities as needed
  ];

  const mapOptions = {
    mapTypeControl: true,
    mapTypeControlOptions: {
      style: google.maps.MapTypeControlStyle.HORIZONTAL_BAR,
      position: google.maps.ControlPosition.TOP_CENTER,
    },
    styles: [
      // Add your custom map styles here
    ],
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Weather App</h1>
      </header>
      <main className="app-content">
        <div className="location-dropdown">
          <Select
            placeholder="Select a location"
            options={cities}
            onChange={handleLocationChange}
          />
        </div>
        {weatherData && weatherData.current ? (
          <div>
            <h2>Current Weather</h2>
            <div className="weather-card">
              <h3>{weatherData.location.name}</h3>
              <p className="temperature">{weatherData.current.temp_c}Â°C</p>
              <p className="condition">{weatherData.current.condition.text}</p>
            </div>
          </div>
        ) : (
          <div>Loading...</div>
        )}
        <div className="map-container">
          {mapCenter && (
            <LoadScript googleMapsApiKey={googleMapsApiKey} libraries={libraries}>
              <GoogleMap center={mapCenter} zoom={12} mapContainerStyle={{ height: '400px' }} options={mapOptions}>
                <Marker position={mapCenter} />
              </GoogleMap>
            </LoadScript>
          )}
        </div>
      </main>
      <footer className="app-footer">
        <p>Powered by React</p>
      </footer>
    </div>
  );
};

export default App;
